public class t2 {

    /**
     * @param args
     */
    public static void main(final String[] args) {
        // TODO Auto-generated method stub
        int i = 0;
        // final int x = i+++++i;
        // System.out.println(x);
        i = 0;
        System.out.println(i++ + ++i);
        i = 0;
        System.out.println(i++ + +i);
        i = 0;
        System.out.println(i + + ++i);
        i = 0;
        System.out.println(i-- - -i);
        i = 0;
        System.out.println(i - - - --i);
        i = 0;
        System.out.println(+i);
    }
}
